import React from 'react'
import { View, Text,  StyleSheet, ScrollView, ImageBackground,Image} from 'react-native';
import Search from './Search';
import Menu1 from './Menu1'
import Dashboard from './Dashboard'
import Base from './Base'

const menu=(props) =>{
    return (
<ScrollView  style={styles.container}>
    <ImageBackground  source={require('../assets/Arriere.png')}style={styles.action}>
    <Text style={styles.temText}> AGROTECH SMART IRRIGATION APP</Text>
    <View style={styles.temView3}>
    <Text style={styles.temText1}> Station:{"\n"}AIT AMIRA</Text>
    
    <Image source={require('../assets/Position1.png')} style={styles.Image} />
    </View>
    <Text style={styles.temText4}> Chercher d'autre station</Text>

    <Search/>
  <Base/>
    </ImageBackground>
    <View style={styles.temView2}>
      <View style={styles.temText5}>
        <Menu1/>
        </View>
        <Dashboard picture={require('../assets/Ray.png')}/>
    </View>
    
</ScrollView >
    
    );
};
export default menu;
const styles = StyleSheet.create({
    action: {
       height:350,
       width:365,
       paddingTop:20
    },
    text: {
        
    },
    temView2: {
        flexDirection: 'row',
        marginTop:100
      },
      temView3: {
        flexDirection: 'row',
        
      },
      temText: {
        fontFamily: "Algerian",
        fontSize:18,
        textAlign:"center",
        color:'#FFFFFF',
        marginBottom:10,

      },
      temText4: {
        fontFamily: "Open Sans*",
        fontSize:17,
        color:'#FFFFFF',
        margin:5,
        marginLeft:20

      },
      temText1: {
       
        fontSize:40,
        color:'#FFFFFF',
        margin:10,
        marginBottom:0

      },
      temText5: {
       
       marginLeft:10,
       marginRight:40

      },
    container: {
        flex:1,  
        
    },
    Image:{
      marginLeft:40,
      marginTop:20
     
    }
}
);
