import React, { useState } from 'react';
import { SafeAreaView, StyleSheet, ScrollView, View, Text, TextInput,} from 'react-native';
 import SearchDropDown from './Components/SearchDropDown'
 import { getFilmsFromApiWithSearchedText } from './API/TMDBApi'
 export default function App() {
   const [dataSource] = useState([])
   const [filtered, setFiltered] = useState(dataSource)
   const [searching, setSearching] = useState(false)
   const onSearch = (text) => {
     if (text) {
       setSearching(true)
       const temp = text.toLowerCase()
      // Seulement si le texte recherchÃ© n'est pas vide
        getFilmsFromApiWithSearchedText(temp).then(data => {
            this.setState({dataSource: data.results })
        })
       const tempList = dataSource.filter(item => {
         if (item.match(temp))
           return item
       })
       setFiltered(tempList)
     }

     else {
       setSearching(false)
       setFiltered(dataSource)
     }

   }

   return (
     <View style={styles.container}>

       <TextInput
         style={styles.textInput}
         placeholder="Search"
         placeholderTextColor='white'
         onChangeText={onSearch}

       />

       {
         searching &&
         <SearchDropDown
           onPress={() => setSearching(false)}
           dataSource={filtered} />
       }
     </View>
   )
 }


 const styles = StyleSheet.create({
   container: {
     // justifyContent: 'center',
     alignItems: 'center',
     marginTop: '20%',
     flex: 1
   },
   textInput: {
     backgroundColor: '#BFBFBF',
     width: '80%',
     borderRadius: 5,
     height: 50,
     fontSize: 20,
     fontWeight: 'bold',
     paddingHorizontal: 10,
   },
 });
