import React, { useState } from 'react';
import { Text,StyleSheet, View,Image,ImageBackground,Picker} from 'react-native';
import Icons from './Icons';
import DropDown, {
  Select,
  Option,
  OptionList,
} from 'react-native-option-select';
const Entete =props => {
  const [selectedValue, setSelectedValue] = useState("java");
        return (     
<ImageBackground source={require('../assets/Arriere.png')} style={styles.backgroundStyle} >
           <Text style={styles.itemText1}> Bienvenue Ahmed</Text>
           <View style={styles.container}>
           <Picker
        selectedValue={selectedValue}
        style={{ height: 50, width: 50,color:'black' }}
        onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
      >
        <Picker.Item label="Java" value="java" />
        <Picker.Item label="JavaScript" value="js" />
      </Picker>
      <Text style={styles.itemText2}>Lundi 12 Mars 2021</Text>  
    </View>
           <View style={styles.itemView1}>
           <Image source={require('../assets/vent7.png')} style={styles.ba1}/>  
           <Image source={require('../assets/vent5.png')} style={styles.ba1}/> 
           <Image source={require('../assets/vent6.png')} style={styles.ba1}/>  
           </View>
           <Select
            width={250}
            
            defaultValue="Select a Province in Canada ..."
            >
           
            <Option value = {"alberta"}>Alberta</Option>
            <Option>British Columbia</Option>
            <Option>Manitoba</Option>
            <Option>New Brunswick</Option>
            <Option>Newfoundland and Labrador</Option>
            <Option>Northwest Territories</Option>
            <Option>Nova Scotia</Option>
            <Option>Nunavut</Option>
            <Option>Ontario</Option>
            <Option>Prince Edward Island</Option>
            <Option>Quebec</Option>
            <Option>Saskatchewan</Option>
            <Option>Yukon</Option>
          </Select>
        <Icons/>
</ImageBackground>
        );
      };
     
    // create style to component
    const styles = StyleSheet.create({
      backgroundStyle: {
        flex: 1,
        width: 400,
        height: 300,
        resizeMode: 'cover',
      },

      itemText1: {
        color: '#FFFFFF',
        marginTop: 20,
        marginLeft: 40,
        fontSize:20
      },
      itemText2: {
        color: '#FFFFFF',
        marginLeft: 200,
        fontSize:10,
        borderRadius: 50,
      },
      itemView1: {
       
        flexDirection: 'row',
        marginTop:10,
        marginLeft:30
      },
      ba1:{
        marginRight:30
      },
      container: {
        flex: 1,
      marginBottom:90,
        alignItems: "center",
       
      },
      Picker:{
        height:50,
        width:100,
        backgroundColor: '#FFFFFF',
        borderRadius: 50,
      },
   
      });
    //Export to component so we can use it elsewhere in our project
    export default  Entete ;
