import React, { useState ,useEffect} from 'react';
import { Text,StyleSheet, View,ScrollView ,Image,ImageBackground,Picker,Button} from 'react-native';
import Icons from './Icons';
const Entete =props => {
  const [selectedValue, setSelectedValue] = useState("A");
  const stations = [
    { name: 'A' },
    { name: 'B' },
    { name: 'Other' }
  ];
  this.state = {
    films: [],
  }
  const DATA=[
    { name: '29' },
    { name: '2' },
    { name: '12' },
    { name: '17' },
    { name: '22' },
    { name: '33' }
];

  useEffect(() => {
    //getStationFromApi(selectedValue).then(data => {
      //this.setState({ stations: data.results })
 // })
 getFilmsFromApiWithSearchedText ("Room").then(data => {
  this.setState({ films: data.results })
})
 //getDATAFromApi(selectedValue).then(data => {
      //this.setState({ DATA: data.results })
 // })
    console.log(selectedValue);

  });


 return (            
  <ImageBackground source={require('../assets/Arriere.png')} style={styles.backgroundStyle} >
    <ScrollView >  
      <Text style={styles.itemText1}> Bienvenue Ahmed</Text>
      <View style={styles.container}>
         <Picker
        selectedValue={selectedValue}
        style={{ height: 40, width: 180 }}
        onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
         >
           {stations.map((stations, key) => (
                      <Picker.Item
                                 key={key}
                                 label="java"
                                 value={this.films.original_language}
                      />
                                           ))}
             
      </Picker>
      <Image source={require('../assets/position.png')}    style={{ height: 45}}/> 
      </View>
      <Text style={styles.itemText2}>Lundi 12 Mars 2021</Text>  
    
      <View style={styles.itemView1}>
           <Image source={require('../assets/vent7.png')} style={styles.ba1}/>  
           <Image source={require('../assets/vent5.png')} style={styles.ba1}/> 
           <Image source={require('../assets/vent6.png')} style={styles.ba1}/>  
      </View> 
      <View style={styles.itemView2}>  
      <Icons  title="temperature" data="29°C" imageSource1={require('../assets/soliel.png')} imageSource2={require('../assets/temperature.png')}/>
      <Icons  title="Précipitation" data="2mm" imageSource1={require('../assets/precipitation.png')} imageSource2={require('../assets/precipitation1.png')}/>
     </View>

     <View style={styles.itemView2}>  
      <Icons  title="Humidité relative" data="12%" imageSource1={require('../assets/vent1.png')} imageSource2={require('../assets/humidité.png')}/>
      <Icons  title="Rayonnement" data="17 W/m" imageSource1={require('../assets/song.png')}  imageSource2={require('../assets/rayoonnement.png')} />
     </View>

     <View style={styles.itemView2}>  
      <Icons  title="Evatranspiration" data="4mm" imageSource1={require('../assets/rayonnement.png')}  imageSource2={require('../assets/temperature.png')}/>
      <Icons  title="Vent" data="29" imageSource1={require('../assets/vent.png')}  imageSource2={require('../assets/temperature.png')}/>
     </View>
    </ScrollView >  
  </ImageBackground>
  
        );
      };
     
    // create style to component
    const styles = StyleSheet.create({
      backgroundStyle: {
        flex: 1,
        width: 400,
        height: 300,
        resizeMode: 'cover',
      },

      itemText1: {
        color: '#FFFFFF',
        marginTop: 20,
        marginLeft: 40,
        fontSize:20
      },
      itemText2: {
        color: '#FFFFFF',
        marginBottom:40,
        marginLeft: 250,
        marginTop:10,
        fontSize:10,
        borderRadius: 50,
      },
      itemView1: {
        flexDirection: 'row',
        marginLeft:30
      },
      ba1:{
        marginRight:30
      },
      
      container: {
        flex: 1,
        backgroundColor: '#FFFFFF',
        borderRadius: 50,
        width:150,
        height:40,
        marginLeft:40,
        marginTop:10,
        flexDirection: 'row',
      },
      itemView2: {
        flexDirection: 'row'
      },
      });
    //Export to component so we can use it elsewhere in our project
    export default  Entete ;
