import React,{ useState, useEffect } from 'react'
import { View, Text, TouchableOpacity,  StyleSheet,Image,} from 'react-native';
import DateA from './DateA' ;
const Menu1=(props) =>{
    const [currentDate, setCurrentDate] = useState('');
    const [currentMonth, setCurrentMonth] = useState('');
    useEffect(() => {
        setCurrentDate(
            new Date().getDate()
        );
        setCurrentMonth(
            new Date().getMonth()+1
        );
      }, []);
    return (
        <View>
        <View style={{flex: 1}}>
        <Image
         style={{
           alignItems:"center",
           alignContent:"center",
           
         }}
         source={require('../assets/Date.png')}
         />
         <View style={{position: 'absolute',marginTop:3}}>
         <DateA date2={currentDate-1+'\n'+'0'+currentMonth}  
         />
         </View>
         <View style={{position: 'absolute',marginTop:49}}>
         <TouchableOpacity style={{position: 'absolute'}}>
         <Text style={{fontSize: 16,width:40,paddingLeft:10,marginLeft:11}}>{currentDate+'\n'+'0'+currentMonth}  </Text>

         </TouchableOpacity>
         
         </View>
         <View style={{position: 'absolute',marginTop:100}}>
         <DateA date2={currentDate+1+'\n'+'0'+currentMonth}   
         />
         </View>
         <View style={{position: 'absolute',marginTop:159}}>
         <DateA date2={currentDate+2+'\n'+'0'+currentMonth}   
         />
         </View>
        
        

        </View>

        
        </View>

    
    );
}


;
export default Menu1;
const styles = StyleSheet.create({
    container:{
         
      marginTop:3, 
         },
       

   
  
}
);
