import React, { useState ,useEffect} from 'react';
import { Text,StyleSheet, View,ScrollView ,Image,ImageBackground,Picker,StatusBar,Dimensions} from 'react-native';
import Serch from './Serch';
import {widthPercentageToDP as wp, heightPercentageToDP as hp} from 'react-native-responsive-screen';
const Entete =(props,navigation) => {
  const [selectedValue, setSelectedValue] = useState("A");
  const [films, setFilms] = useState();
  const {width, height} = Dimensions.get('window')
  const stations = [
    { name: 'A' },
    { name: 'B' },
    { name: 'Other' }
  ];
 
  const DATA=[
    { name: '29' },
    { name: '2' },
    { name: '12' },
    { name: '17' },
    { name: '22' },
    { name: '33' }
];
const [hidden, setHidden] = useState(true);
  useEffect(() => {
   // navigation.setOptions({headerShown:false},[navigation]);
    //setHidden(hidden);
    //.setHidden(hidden)
    //getStationFromApi(selectedValue).then(data => {
      //this.setState({ stations: data.results })
 // })
 //getFilmsFromApiWithSearchedText ("Room").then(data => {
 // this.setState({ films: data.results })
//})
 //getDATAFromApi(selectedValue).then(data => {
      //this.setState({ DATA: data.results }
 // })
    console.log(selectedValue);

  });


 return (            
    <ScrollView  >  
 <StatusBar
        animated={true}
        backgroundColor="#61dafb"
        
        
        hidden={hidden} />
    <ImageBackground source={require('../assets/acceuil.png')} style={styles.action}>
 
      <Text style={styles.itemText1}> Bienvenue Ahmed</Text>
     
      <View style={styles.container}>
         <Picker
        selectedValue={selectedValue}
        style={{ height: 40, width: 180 }}
        onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
         >
           {stations.map((stations, key) => (
                      <Picker.Item
                                 key={key}
                                 label="java"
                                 value={stations.name}
                      />
                                           ))}
             
      </Picker>
      <Image source={require('../assets/position.png')}    style={{ height: 45}}/> 
      </View>
      <Text style={styles.itemText2}>Lundi 12 Mars 2021</Text>  
    
      <View style={styles.itemView1}>
           <Image source={require('../assets/vent7.png')} style={styles.ba1}/>  
           <Image source={require('../assets/vent5.png')} style={styles.ba1}/> 
           <Image source={require('../assets/vent6.png')} style={styles.ba1}/>  
      </View> 
      <View style={styles.itemView2}>  
      <Serch 
      onPress={()=>props.navigation.navigate('Profile2')}
      title="temperature" data="29°C" imageSource1={require('../assets/soliel.png')} imageSource2={require('../assets/temperature.png')}/>
      <Serch  
      onPress={()=>props.navigation.navigate('Profile5')} 
      title="Précipitation" data="2mm" imageSource1={require('../assets/precipitation.png')} imageSource2={require('../assets/precipitation1.png')}/>
     </View>
     </ImageBackground>
     
     <View style={styles.itemView4}>  
      <Serch
       onPress={()=>props.navigation.navigate('Profile4')}
      title="Humidité relative" data="12%" imageSource1={require('../assets/vent1.png')} imageSource2={require('../assets/humidité.png')}/>
      <Serch  
      onPress={()=>props.navigation.navigate('Profile')}
      title="Rayonnement" data="17 W/m" imageSource1={require('../assets/song.png')}  imageSource2={require('../assets/rayoonnement.png')} />
     </View>

     <View style={styles.itemView3}>  
      <Serch 
       onPress={()=>props.navigation.navigate('Profile3')}
      title="Evatranspiration" data="4mm" imageSource1={require('../assets/rayonnement.png')}  imageSource2={require('../assets/temperature.png')}/>
      <Serch
      onPress={()=>props.navigation.navigate('Profile1')}  
      title="Vent" data="29" imageSource1={require('../assets/vent.png')}  imageSource2={require('../assets/temperature.png')}/>
     </View>
    </ScrollView >  
        );
      };
     
    // create style to component
    const styles = StyleSheet.create({
      backgroundStyle: {
        flex: 1,
       // width: wp('84.5%'),
      
      },
      itemText1: {
        color: '#FFFFFF',
        marginTop: 50,
        marginLeft: 40,
        fontSize:20
      },
      itemText2: {
        color: '#FFFFFF',
        marginBottom:40,
        marginLeft: 250,
        fontSize:10,
        borderRadius: 50,
      },
      itemView1: {
        flexDirection: 'row',
        marginLeft:30
      },
      ba1:{
        marginRight:30
      },
      
      container: {
        backgroundColor: '#FFFFFF',
        borderRadius: 50,
        width:150,
        height:40,
        marginLeft:20,
        marginTop:10,
        flexDirection: 'row',
      },
      itemView2: {
        flexDirection: 'row'
      },
      itemView3: {
        flexDirection: 'row',
        marginTop:20
      },
      itemView4: {
        flexDirection: 'row',
        marginTop:130
      },
      action: {
        height:350,
        width:370,
        alignContent:"center",
        marginRight:100,
        flex:1,
        },    
      });
    //Export to component so we can use it elsewhere in our project
    export default  Entete ;
