//import libraries we need to  create a component
import React, { useState } from 'react';
import { StyleSheet, View, TextInput,FlatList,Text} from 'react-native'
import { getFilmsFromApiWithSearchedText } from '../API/TMDBApi'
import { Feather } from '@expo/vector-icons'; 

class Search extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      films: [],
      searchedText: "" 
    }
  }

_searchTextInputChanged(text) {
  this.setState({ searchedText: text })
  getFilmsFromApiWithSearchedText(this.state.searchedText).then(data => {
      this.setState({ films: data.results })
  })

}


  render() {
    const [filtered, setFiltered] = useState(dataSource)
    const [searching, setSearching] = useState(false)
    const onSearch = (text) => {
      if (text) {
        getFilmsFromApiWithSearchedText(this.state.searchedText).then(data => {
          this.setState({ films: data.results })
      })
        setSearching(true)
        const temp = text.toLowerCase()
  
        const tempList = films.filter(item => {
          if (item.match(temp))
            return item
        })
        setFiltered(tempList)
      }
      else {
        setSearching(false)
        setFiltered(films)
      }
  
    }
   
  
    return (
    
      <View style={styles.itemSearch}>

      <Feather name="search"  style={styles.itemIcon}/>
      

          
            <TextInput
                placeholder="Chercher"
                onChangeText={onSearch}
                platform="android" 
             /> 
       {
        searching &&
        <SearchDropDown
          onPress={() => setSearching(false)}
          dataSource={filtered} />
      }  
       </View>
     
      
    )
    }
  } 

// create style to component
const styles = StyleSheet.create({
  itemSearch: {
    margin: 15,
    borderColor: '#FFFFFF',
    borderWidth: 1,
    backgroundColor: '#FFFFFF',
    height:40,
    borderRadius: 5,
    flexDirection: 'row'
  },
  itemTexinput:{
    backgroundColor: '#FFFFFF',
  },

  itemIcon:{
    fontSize:30,
    color:"#C0C0C0",
    alignSelf:'center',
    marginHorizontal:15
  },
  itemMenu: {
    margin: 2000,
    borderColor: '#FFFFFF',
    borderWidth: 1,
    backgroundColor: '#FFFFFF',
    height:40,
    borderRadius: 5,
    flexDirection: 'row'
  },
  });

//Export to component so we can use it elsewhere in our project
export default  Search;
