//import libraries we need to  create a component
import React from 'react';
import { StyleSheet, Text, View, FlatList, Button,TextInput } from 'react-native'
import { getFilmsFromApiWithSearchedText } from '../API/TMDBApi'
//create component function  that return some 'jsx' like html  or use const component=function(){};
class Search extends React.Component {
  //Pour afficher les films de l'API, il faut déjà les stocker dans une propriété de notre component
  constructor(props) {
    super(props)
    // Ici on va créer les propriétés de notre component custom Search
    this._films = []
  }

  // Components/Search.js

_loadFilms() {
  getFilmsFromApiWithSearchedText("star").then(data => console.log(data));
  this._films = data.results
  this.forceUpdate()
}

  render() {
    return (
      <View style={styles.main_container}>
        <TextInput style={styles.textinput} placeholder='Titre du film'/>
        <Button title='Rechercher' onPress={() => this._loadFilms()}/>
        <FlatList
             data={this._films}
             keyExtractor={(item) => item.id.toString()}
             renderItem={({item}) => <Text>{item.title}</Text>}
        />
      </View>
    )
  }
}
// create style to component
const styles = StyleSheet.create({
  main_container: {
    flex: 1,
    marginTop: 60
  },
  textinput: {
    height: 40,
    borderColor: '#000000',
    borderWidth: 1,
    width:350
  }
  });
//Export to component so we can use it elsewhere in our project
export default  Search;
