       padding:20,
       shadowColor: "#000",
       shadowOffset: {
         width: 0,
         height: 16,
       },
       shadowOpacity: 0.58,
       shadowRadius: 16.00,
       
       elevation: 24,
       
      },
      itemFont2: {
        color:'blue',
        fontSize:24,
        marginTop:20,
        marginLeft:40,
        backgroundColor: 'white',
        borderRadius: 500,
        width:70,
        padding:20,
        shadowColor: "#000",
        shadowOffset: {
        width: 0,
        height: 16,
