//import libraries we need to  create a component
import React, { useState } from 'react';
import { StyleSheet, View, TextInput,FlatList,Text} from 'react-native'
import { getFilmsFromApiWithSearchedText } from '../API/TMDBApi'
import { Feather } from '@expo/vector-icons'; 
import SearchDropDown from './SearchDropDown'
class Search extends React.Component {
  constructor(props) {
    super(props)
    this.searchedText = "" 
    this.state = {
      films: [],
      searching: false
    }
  }



  render() {

    const onSearch = (text) => {
      if (text) {
        this.searchedText = text 
        getFilmsFromApiWithSearchedText(this.searchedText).then(data => {
          this.setState({ films: data.results })
      })
      this.setState({ searching: true })
        
      }
      else {
        this.setState({ searching: false })
      }
  
    }
   
  
    return (
    
      <View style={styles.itemSearch}>

      <Feather name="search"  style={styles.itemIcon}/>
      

          
            <TextInput
                placeholder="Chercher"
                onChangeText={onSearch}
                platform="android" 
             /> 
   <SearchDropDown
          onPress={() => setSearching(false)}
          dataSource={this.state.films} />
      


       </View>
     
      
    )
    }
  } 

// create style to component
const styles = StyleSheet.create({
  itemSearch: {
    margin: 15,
    borderColor: '#FFFFFF',
    borderWidth: 1,
    backgroundColor: '#FFFFFF',
    height:40,
    borderRadius: 5,
    flexDirection: 'row'
  },
  itemTexinput:{
    backgroundColor: '#FFFFFF',
  },

  itemIcon:{
    fontSize:30,
    color:"#C0C0C0",
    alignSelf:'center',
    marginHorizontal:15
  },
  itemMenu: {
    margin: 2000,
    borderColor: '#FFFFFF',
    borderWidth: 1,
    backgroundColor: '#FFFFFF',
    height:40,
    borderRadius: 5,
    flexDirection: 'row'
  },
  });

//Export to component so we can use it elsewhere in our project
export default  Search;
