//import libraries we need to  create a component
import React from 'react';
import { StyleSheet, View, TextInput,FlatList,Text} from 'react-native'
import { getFilmsFromApiWithSearchedText } from '../API/TMDBApi'
import { Feather } from '@expo/vector-icons'; 
import DropDownPicker from 'react-native-dropdown-picker';
import {
  MenuContext,
  Menu,
  MenuOptions,
  MenuOption,
  MenuTrigger,
} from 'react-native-popup-menu';

class Search extends React.Component {
  //Pour afficher les films de l'API, il faut déjà les stocker dans une propriété de notre component
  constructor(props) {
    super(props)
    // Ici on va créer les propriétés de notre component custom Search
    //this.state = { films: [] }
    this.state = {
      films: [],
      searchedText: "" // Initialisation de notre donnée searchedText dans le state
    }

    this.state = {
      country: 'uk'
  }
  }
 
  

  // Components/Search.js


_searchTextInputChanged(text) {
  this.setState({ searchedText: text })
// Seulement si le texte recherché n'est pas vide
  getFilmsFromApiWithSearchedText(this.state.searchedText).then(data => {
      this.setState({ films: data.results })
  })

}


  render() {
    

    const Searching =(text)=>
    {
      setSearching(true)
    
    }
    var data = [["Big Data", "Hadoop", "Spark", "Hive"], ["Data Science" ,"Python","Ruby"]];
    return (
      <View >
      <View style={styles.itemSearch}>

      <Feather name="search"  style={styles.itemIcon}/>
      <TextInput
        placeholder="Chercher"
        onChangeText={(text) => this._searchTextInputChanged(text)}
    
        platform="android" 
       />
       </View>
       <View>
        <FlatList
          data={this.state.films}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({item}) => <Text>{item.title}</Text>}
        />
        </View>

        <DropDownPicker
    items={[
        {label: 'USA', value: 'usa'},
        {label: 'UK', value: 'uk'},
        {label: 'France', value: 'france'},
    ]}
    defaultValue={this.state.country}
    

    onChangeItem={item => this.setState({
        country: item.value
    })}
/>
<MenuContext >
        <View>
          <Menu>
            <MenuTrigger text="Open menu" />

            <MenuOptions>
              <MenuOption text="Save" />
              <MenuOption >
                <Text>Delete</Text>
              </MenuOption>
              <MenuOption
                text="Disabled"
              />
            </MenuOptions>
          </Menu>
        </View>
      </MenuContext>

      </View>
      
    )
  }
}
// create style to component
const styles = StyleSheet.create({
  itemSearch: {
    margin: 15,
    borderColor: '#FFFFFF',
    borderWidth: 1,
    backgroundColor: '#FFFFFF',
    height:40,
    borderRadius: 5,
    flexDirection: 'row'
  },
  itemTexinput:{
    backgroundColor: '#FFFFFF',
  },

  itemIcon:{
    fontSize:30,
    color:"#C0C0C0",
    alignSelf:'center',
    marginHorizontal:15
  }
  });
//Export to component so we can use it elsewhere in our project
export default  Search;
